      limit ${criterion.value},${criterion.secondValue}



/* maxscale route to master */;


select 

from order_depot_ship
where depot_code = #{depotCode,jdbcType=VARCHAR}
  and invoice_no = #{invoiceNo,jdbcType=VARCHAR}
  and order_sn = #{orderSn,jdbcType=VARCHAR}
/* maxscale route to master */;

mapper.delete:

delete from order_depot_ship
where depot_code = #{depotCode,jdbcType=VARCHAR}
  and invoice_no = #{invoiceNo,jdbcType=VARCHAR}
  and order_sn = #{orderSn,jdbcType=VARCHAR}
