mapper.select:
SELECT count(*)
FROM order_reward_point_goods_master master
left join order_reward_point_goods_detail detail
on master.order_sn = detail.order_sn where master.buyer_sn=#{buyerSN}

    and master.create_time<=#{endOrderTime} and master.create_time>=#{beginOrderTime}


    and master.order_status=${orderStatus}

SELECT master.id, master.order_sn, master.buyer_sn, master.cancel_sn, master.cancel_time, master.total_point, master.order_status, master.receiver_name, master.receiver_tel, master.receiver_tel, master.receiver_address, master.create_time, master.cancel_time, master.comment, detail.goods_sn, detail.goods_name, detail.goods_brand, detail.need_point, detail.sale_count, detail.express_company, detail.express_sn, detail.express_time FROM order_reward_point_goods_master master left join order_reward_point_goods_detail detail on master.order_sn = detail.order_sn where master.buyer_sn=#{buyerSN}
and master.create_time<=#{endOrderTime} and master.create_time>=#{beginOrderTime}
and master.order_status=${orderStatus}
order by ${order} ${sort}
limit ${start},${pageSize}

mapper.update:
update order_reward_point_goods_master
set cancel_time=#{cancelTime},
    order_status=4,
    cancel_sn=#{cancelSN}
where order_sn = #{orderSN}
  and buyer_sn = #{buyerSN}
