mapper.select:
select count(*) from order_reward_point_goods_master where buyer_sn=#{buyerSN}

    and create_time<=#{endOrderTime} and create_time>=#{beginOrderTime}


    and order_status=${orderStatus}


    order by ${order} ${sort},id

limit ${start},${pageSize}

select * from order_reward_point_goods_master where buyer_sn=#{buyerSN}

    and create_time<=#{endOrderTime} and create_time>=#{beginOrderTime}


    and order_status=${orderStatus}


    order by ${order} ${sort},id

limit ${start},${pageSize}

mapper.update:
update order_reward_point_goods_master
set cancel_time=#{cancelTime},order_status=4, cancel_sn=#{cancelSN}
where order_sn=#{orderSN} and buyer_sn=#{buyerSN}
