mapper.select:

select

  distinct


from order_rider_distribute_log




  order by ${orderByClause}




      limit ${criterion.value},${criterion.secondValue}

mapper.sql:
  		where ord.master_order_sn = oi.master_order_sn
  			and ord.master_order_sn = oie.master_order_sn

				and ord.channel_code = #{channelCode}


				and ord.channel_code in

					#{item}



				and ord.shop_code = #{storeCode}


				and ord.shop_code in

					#{item}



				and ord.master_order_sn = #{orderNo}


				and ord.master_order_sn = #{masterOrderSn}


				and ord.order_status = #{orderStatus}


				and ord.create_time > #{startTime}


				and ord.create_time < #{endTime}


				and ord.tms_code = #{tmsCode}


				and ord.tms_code in

					#{item}

mapper.select:
select ord.log_id as logId, ord.master_order_sn as masterOrderSn, ord.order_out_sn as orderOutSn, ord.channel_code as channelCode, ord.shop_code as shopCode, ord.client_order_sn as clientOrderSn, ord.order_status as orderStatus, ord.add_time as addTime, ord.create_time as createTime, ord.accept_time as acceptTime, ord.fetch_time as fetchTime, ord.finish_time as finishTime, ord.cancel_time as cancelTime, oie.shop_name as shopName, ord.tms_code as tmsCode, ord.tms_name as tmsName from order_rider_distribute_log ord, master_order_info oi, master_order_info_extend oie
order by ord.add_time desc
limit ${start}, ${limit}
