mapper.select:

select

  distinct


from purchase_order




  order by ${orderByClause}




      limit ${criterion.value},${criterion.secondValue}

select count(1) from purchase_order where order_type = 0 and purchase_status = 0 and pay_status = 2 and ship_Status = 5
and create_time >= #{beginTime}
and create_time < #{endTime}

select purchase_order_code purchaseOrderCode, order_sn orderSn, master_order_sn masterOrderSn, purchase_status purchaseStatus, add_time addTime, create_time createTime, pay_status payStatus, ship_Status shipStatus, order_type orderType, goods_count goodsCount, total_fee totalFee from purchase_order where order_type = 0 and purchase_status = 0 and pay_status = 2 and ship_Status = 5
and create_time >= #{beginTime}
and create_time < #{endTime}
order by create_time desc
limit #{start}, #{limit}
