mapper.select:
SELECT a.*, GROUP_CONCAT(DISTINCT oreturn.return_sn) returnSns, GROUP_CONCAT(DISTINCT moi.master_order_sn) exChangeSns, returnchange.return_type returnType FROM( select oi.master_order_sn orderSn, DATE_FORMAT(oi.add_time,'%Y-%m-%d %T') orderCreateTime, oi.total_payable totalPayable, oi.total_fee totalfee, oi.pay_total_fee payTotalfee, op.pay_name paymentMethod, oai.consignee receiver, oi.surplus surplus, oi.referer referer, oi.bonus bonus, oi.money_paid moneyPaid, oi.goods_amount goodsAmount, oi.shipping_total_fee shippingTotalFee, oi.discount discount, oi.goods_count goodsCount, sum(grc.return_sum) returnGoodsCount, op.pay_id payId, op.pay_lasttime payLastTime, oie.is_review isReview, oie.free_post_type freePostType, oi.order_status orderStatus, oi.pay_status payStatus, oi.user_id userId, oi.ship_status shipStatus, oi.order_type orderType, oi.trans_type transType, oi.integral_money integralMoney, oi.integral integral, oi.source source, oi.order_from channelCode, oi.shop_name channelName, oi.goods_sale_type goodsSaleType, oi.price_change_status priceChangeStatus, oie.is_advance isAdvance, oie.customer_contract_num customerContractNum, oi.expected_ship_date expectedShipDate, oie.user_pay_apply userPayApply, oi.tax, oi.bv_value bvValue, oi.need_audit needAudit, oai.inv_consignee invConsignee, oai.inv_mobile invMobile, oai.inv_address invAddress, oie.company_type companyType, oie.bo_id boId from
master_order_info
master_order_info_history
oi left join
master_order_address_info
master_order_address_info_history
oai on oi.master_order_sn = oai.master_order_sn left join
master_order_goods
master_order_goods_history
og on og.master_order_sn=oi.master_order_sn left join
master_order_pay
master_order_pay_history
op on op.master_order_sn=oi.master_order_sn left join master_order_info_extend oie on oie.master_order_sn=oi.master_order_sn left join goods_return_change grc on grc.order_sn=oi.master_order_sn where oie.is_del = 0 and oi.channel_code = #{siteCode,jdbcType=VARCHAR}
and oi.user_id= #{userId,jdbcType=VARCHAR}
and oi.order_from = #{shopCode,jdbcType=VARCHAR}
and oi.master_order_sn = #{orderSn,jdbcType=VARCHAR}
and og.custom_code = #{goodsSn,jdbcType=VARCHAR}
and og.customer_material_code = #{materielNo}
and oi.add_time >= #{startTime}
and oi.add_time <= #{endTime}
and oi.master_order_sn in
#{orderSn}
and oie.customer_contract_num = #{contractNum}
and oi.sign_status = #{signStatus}
and oi.goods_sale_type = #{goodsSaleType}
and oi.price_change_status = #{priceChangeStatus}
and oie.company_code = #{companyId}
and oie.bo_id = #{boId}
and oi.pay_status in (0,1) and oi.order_status=0
and oi.pay_status = 2 and oi.order_status In (0,1,5,6) and oi.ship_status in (0,1)
and oi.ship_status in (2,3,4)
and oi.ship_status = 5
and oi.ship_status = 5 and oie.is_review = 0
and oi.order_status = 2
and oi.order_type = 2 and oi.total_payable > 0
and oi.order_type = 2 and oi.total_payable <= 0 and oi.ship_status <5
and oi.order_type = 2 and oi.ship_status = 5
GROUP BY oi.master_order_sn order by oi.add_time desc
limit #{start}, #{pageSize}
) a left join order_return oreturn on oreturn.master_order_sn=a.orderSn AND oreturn.return_order_status != 4 left join master_order_info moi on moi.relating_original_sn=a.orderSn AND moi.order_type=2 AND moi.order_status != 2 left join goods_return_change returnchange on returnchange.order_sn=a.orderSn GROUP BY a.orderSn order by a.orderCreateTime DESC;

