select og.order_sn subOrderSn, og.goods_price goodsPrice, og.goods_price_no_tax goodsPriceNoTax, og.transaction_price transactionPrice, og.transaction_price_no_tax transactionPriceNoTax, og.settlement_price settlementPrice, og.goods_sn goodsSn, og.custom_code skuSn, og.goods_name goodsName, og.goods_thumb goodsUrl, og.goods_number goodsNum, og.goods_size_name goodsSize, og.goods_color_name goodsColor, og.discount discountedPrice, og.pay_points payPoints, og.extension_code extensionCode, og.integral_money integralMoney, og.integral integral, og.c2m_item c2mItemStr, og.send_number sendNumber, oi.order_from orderFrom, og.bv_value bvValue, og.expected_ship_date expectedShipDate, og.tax, og.tax_rate taxRate, og.input_tax inputTax, og.output_tax outputTax, og.base_bv_value baseBvValue, og.min_buy_num minBuyNum, og.buy_unit buyUnit, og.customer_material_code customerMaterialCode, og.buyer_no as buyerNo, og.buyer_line_no as buyerLineNo, og.distribution_category distributionCategory, og.depot_code depotCode, og.supplier_code supplierCode, og.supplier_name supplierName, og.with_stock_number withStockNumber, og.without_stock_number withoutStockNumber, og.purchases_without_stock_flag purchasesWithoutStockFlag, og.without_stock_delivery_cycle withoutStockDeliveryCycle, og.delivery_cycle deliveryCycle, og.data_sources dataSources, og.brand_id brandId, og.brand_name brandName from
master_order_goods
master_order_goods_history
og left join
master_order_info
master_order_info_history
oi on oi.master_order_sn=og.master_order_sn where 1=1
and og.order_sn=#{orderSn,jdbcType=VARCHAR}
and og.depot_code=#{depotCode,jdbcType=VARCHAR}
and og.master_order_sn=#{masterOrderSn,jdbcType=VARCHAR}
and og.invoice_no=#{invoiceNo,jdbcType=VARCHAR}
and og.is_del=0 GROUP BY og.custom_code, og.extension_code;

select og.goods_price goodsPrice, og.transaction_price transactionPrice, og.settlement_price settlementPrice, og.custom_code skuSn, og.goods_name goodsName, og.goods_thumb goodsUrl, sum(og.goods_number) goodsNum, og.goods_size_name goodsSize, og.goods_color_name goodsColor, og.discount discountedPrice, og.pay_points payPoints, og.extension_code extensionCode, og.integral_money integralMoney, og.integral integral, og.c2m_item c2mItemStr, og.send_number sendNumber from master_order_goods og where 1=1
and og.order_sn=#{orderSn,jdbcType=VARCHAR}
and og.custom_Code=#{customCode,jdbcType=VARCHAR}
GROUP BY og.custom_code,og.extension_code;

select orn.master_order_sn orderSn, orn.return_sn orderReturnSn, orn.pay_status rpayStatus, orn.return_allgoods_count returnAllgoodsCount, orn.return_order_status returnOrderStatus, grc.contact_name contactUser, grc.contact_mobile contactMobile, orf.backBalance backBalance, orn.return_order_status returnOrderStatus, orn.ship_status shipStatus, orn.return_reason returnReason, orn.channel_code shopCode, orn.shop_name shopName, moie.customer_contract_num customerContractNum, DATE_FORMAT(orn.add_time,'%Y-%m-%d %T') orderCreateTime from order_return orn left join order_refund orf on orn.return_sn=orf.relating_return_sn left join (SELECT contact_name, contact_mobile, order_sn FROM goods_return_change WHERE is_del=0 AND `status`=2 GROUP BY order_sn) grc on grc.order_sn=orn.relating_order_sn left join master_order_info_extend moie on moie.master_order_sn=orn.relating_order_sn where orn.user_id= #{userId,jdbcType=VARCHAR} AND orn.site_code = #{siteCode,jdbcType=VARCHAR} and orn.return_type = 1 and orn.process_type = 1 and orn.return_order_status !=4 and orn.return_order_status !=0 and orn.is_del = 0
and orn.channel_code = #{shopCode,jdbcType=VARCHAR}
and orn.return_sn = #{returnSn,jdbcType=VARCHAR}
and orn.pay_status in (0,2)
and orn.pay_status = 1
and orn.return_order_status = 1 and orn.ship_status != 3 and orf.backBalance = 0
and orn.ship_status = 3
and orf.backBalance = 1
GROUP BY orn.return_sn order by orn.add_time desc limit #{start},#{pageSize}

select orn.pay_status   payStatus,
       orn.process_type processType
from order_return orn
         left join order_return_goods org on orn.return_sn = org.relating_return_sn
where orn.relating_order_sn = #{subOrderSn,jdbcType=VARCHAR}
  and org.custom_code = #{sku,jdbcType=VARCHAR};

select DATE_FORMAT(orn.add_time,'%Y-%m-%d %T') applyTime, orn.return_bonus_money bonus, orn.return_shipping returnPostage, orn.return_goods_money returnGoodsMoney, DATE_FORMAT(orn.clear_time,'%Y-%m-%d %T') clearingTime, orn.return_sn orderReturnSn, orn.user_id userId, orn.pay_status payStatus, DATE_FORMAT(ors.quality_time,'%Y-%m-%d %T') qualityTime, DATE_FORMAT(ors.received_time,'%Y-%m-%d %T') receiveTime, ors.return_express returnExpress, ors.return_express_img returnExpressImg, ors.return_invoice_no returnInvoiceNo, orn.return_desc remark, orn.return_reason returnReason, orn.action_user actionUser, orn.return_order_status returnOrderStatus, orn.ship_status shipStatus, orn.channel_code shopCode, orn.shop_name shopName, moai.consignee consignee, grc.contact_name contactUser, grc.contact_mobile contactMobile, moie.customer_contract_num customerContractNum, orf.backBalance backBalance from order_return orn left join order_return_ship ors on orn.return_sn=ors.relating_return_sn left join goods_return_change grc on grc.order_sn=orn.relating_order_sn and grc.is_del=0 and grc.status=2 left join order_refund orf on orn.return_sn=orf.relating_return_sn left join master_order_info_extend moie on moie.master_order_sn=orn.relating_order_sn left join master_order_address_info moai on moai.master_order_sn=orn.relating_order_sn where orn.return_sn= #{orderReturnSn,jdbcType=VARCHAR}
and orn.user_id = #{userId,jdbcType=VARCHAR}
group by orn.return_sn

select user_id from master_order_info oi
where oi.order_from = #{orderFrom,jdbcType=VARCHAR}

    and oi.order_status=#{orderStatus,jdbcType=VARCHAR}


    and oi.clear_time >= #{dateStart,jdbcType=VARCHAR}


    and oi.clear_time <= #{dateEnd,jdbcType=VARCHAR}


    and oi.money_paid >= #{minMoneyPaid,jdbcType=VARCHAR}


    and oi.money_paid < #{maxMoneyPaid,jdbcType=VARCHAR}

limit ${start},${num}

