mapper.select:
    select

    from goods_return_change grc

     inner join master_order_goods_history


     inner join master_order_goods 

    og
    on grc.sku_sn=og.custom_code and grc.order_sn=og.master_order_sn
	WHERE grc.user_id = #{userId,jdbcType=VARCHAR}
	and grc.site_code = #{siteCode,jdbcType=VARCHAR}

		and grc.sku_sn=#{goodsSn,jdbcType=VARCHAR} 


		and grc.status=#{rStatus} 

	and grc.id in (select max(id) from goods_return_change where user_id = #{userId,jdbcType=VARCHAR} 
	group by sku_sn,order_sn) group by grc.id ORDER BY grc.create desc  limit #{start},#{pageSize}

     select count(1) from (select
   	count(*)
   from goods_return_change grc

     inner join master_order_goods_history


     inner join master_order_goods 

    og
    on grc.sku_sn=og.custom_code and grc.order_sn=og.master_order_sn
	WHERE  grc.user_id = #{userId,jdbcType=VARCHAR}
	and grc.site_code = #{siteCode,jdbcType=VARCHAR}

	and grc.sku_sn=#{goodsSn,jdbcType=VARCHAR} 


	and grc.status=#{rStatus} 

	and grc.id in (select max(id) from goods_return_change where user_id = #{userId,jdbcType=VARCHAR} 
	group by sku_sn,order_sn) group by grc.id ORDER BY grc.create desc ) aa

    select
	    grc.order_sn,
	    grc.sku_sn,
	    grc.return_sum,
	    DATE_FORMAT(grc.`create`,'%Y-%m-%d %k:%i:%S') `create`,
	    grc.returnchange_sn,
	    grc.`status`,
	    grc.return_type,
        grc.reason reason,
	    og.transaction_price,
        og.goods_size_name,
        og.goods_color_name
    from goods_return_change grc

     LEFT join master_order_goods_history


     LEFT join master_order_goods 

    og
    on grc.sku_sn=og.custom_code and grc.order_sn=og.master_order_sn
    WHERE grc.user_id = #{userId,jdbcType=VARCHAR}
    and grc.site_code = #{siteCode,jdbcType=VARCHAR}
      and grc.is_del = 0

        and grc.sku_sn=#{goodsSn,jdbcType=VARCHAR} 


        and grc.status=#{rStatus} 


        and grc.order_sn=#{orderSn}


          and grc.return_type=#{returnType}


          and grc.returnchange_sn=#{changeSn}

    and grc.id in (select max(id) from goods_return_change where user_id = #{userId,jdbcType=VARCHAR} 
    group by sku_sn,order_sn) group by grc.id ORDER BY grc.create desc  limit #{start},#{pageSize}

 select count(1) from (select
 count(*)
from goods_return_change grc

  LEFT join master_order_goods_history


  LEFT join master_order_goods 

 og
 on grc.sku_sn=og.custom_code and grc.order_sn=og.master_order_sn
 WHERE  grc.user_id = #{userId,jdbcType=VARCHAR}
 and grc.site_code = #{siteCode,jdbcType=VARCHAR}
   and grc.is_del = 0

 and grc.sku_sn=#{goodsSn,jdbcType=VARCHAR} 


 and grc.status=#{rStatus} 


   and grc.order_sn=#{orderSn}

 and grc.id in (select max(id) from goods_return_change where user_id = #{userId,jdbcType=VARCHAR} 
 group by sku_sn,order_sn) group by grc.id ORDER BY grc.`create` desc ) aa

select `status`, count(1) num from goods_return_change
where  site_code = #{siteCode} and user_id = #{userId} and is_del = 0
group by `status`;
