select grcd.goods_name goodsName, grcd.custom_code customCode, grcd.extension_code extensionCode, grcd.goods_number goodsNumber, grcd.goods_price goodsPrice, grcd.transaction_price transactionPrice, grcd.settlement_price settlementPrice, grcd.discount discount, grcd.goods_size_name goodsSizeName, grcd.goods_color_name goodsColorName, grcd.goods_thumb goodsThumb, grcd.share_bonus shareBonus, grcd.market_price marketPrice, grcd.goods_sn goodsSn, grcd.return_sum returnSum, grcd.returnchange_sn returnChangeSn, grcd.depot_code depotCode, mog.min_buy_num minBuyNum, mog.customer_material_code customerMaterialCode, mog.buy_unit buyUnit, mog.supplier_name supplierName, mog.supplier_code supplierCode, mog.cost_price costPrice from goods_return_change_detail grcd left join master_order_goods mog on grcd.order_sn = mog.master_order_sn and grcd.custom_code = mog.custom_code and grcd.extension_code = mog.extension_code where grcd.returnchange_sn in
#{returnChangeSn}
group by grcd.custom_code, grcd.extension_code
