SELECT DISTINCT oi.master_order_sn FROM master_order_goods og LEFT JOIN master_order_info oi ON oi.master_order_sn = og.master_order_sn LEFT JOIN master_order_info_extend oie ON oie.master_order_sn = og.master_order_sn WHERE oie.is_advance = 1 AND og.custom_code IN
#{item}
AND oi.order_from = #{shopCode ,jdbcType=VARCHAR}

SELECT oinfo.master_order_sn masterOrderSn, oinfo.delivery_time deliveryTime, oinfo.shop_code storeCode FROM ( SELECT moi.master_order_sn, moai.delivery_time, moie.shop_code FROM master_order_info moi, master_order_info_extend moie, master_order_address_info moai, order_distribute od WHERE moi.master_order_sn = od.master_order_sn AND moi.master_order_sn = moie.master_order_sn AND moi.master_order_sn = moai.master_order_sn AND moi.order_status = 1 AND moi.pay_status = 2 AND moi.ship_status = 0 AND moie.is_cac = 0 AND moi.source != 6 AND moi.add_time >= #{startTime} AND moi.add_time <= #{endTime}
AND moie.shop_code = #{storeCode ,jdbcType=VARCHAR}
) oinfo LEFT JOIN order_rider_distribute_log ordl ON oinfo.master_order_sn = ordl.master_order_sn WHERE ordl.master_order_sn IS NULL;

