mapper.select:
SELECT moi.master_order_sn masterOrderSn, moi.channel_code channelCode, moi.order_from shopCode, moi.add_time addTime, moi.order_status orderStatus, oi.order_sn outerOrderSn, moai.consignee receiverName, moai.mobile receiverMobile, moai.province province, moai.city city, moai.address address, ods.depot_code depotCode, ods.shipping_status shipStatus, ods.delivery_time deliveryTime, ods.delivery_confirm_time deliveryConfirmTime, oi.got_status gotStatus, sum(mog.goods_number) goodsCount, moi.total_fee totalFee FROM master_order_info moi, master_order_address_info moai, order_distribute oi, order_depot_ship ods, master_order_goods mog WHERE moi.master_order_sn = oi.master_order_sn AND moi.master_order_sn = moai.master_order_sn AND moi.master_order_sn = mog.master_order_sn AND ods.order_sn = oi.order_sn AND ods.depot_code = mog.depot_code
GROUP BY moi.master_order_sn, oi.order_sn, mog.depot_code order by moi.add_time desc
limit ${start}, ${limit}

SELECT moi.master_order_sn masterOrderSn, moi.channel_code channelCode, moi.order_from shopCode, moi.add_time addTime, moi.user_id userId, moi.order_status orderStatus, oi.order_sn orderSn, moai.consignee receiverName, moai.mobile receiverMobile, ods.depot_code depotCode, ods.shipping_status shipStatus, ods.delivery_time deliveryTime, ods.delivery_confirm_time deliveryConfirmTime, mog.goods_name goodsName, mog.goods_sn goodsSn, mog.custom_code customCode, mog.sap, mog.box_gauge boxGauge, sum(mog.goods_number) goodsNumber FROM master_order_info moi, master_order_address_info moai, order_distribute oi, order_depot_ship ods, master_order_goods mog WHERE moi.master_order_sn = oi.master_order_sn AND moi.master_order_sn = moai.master_order_sn AND moi.master_order_sn = mog.master_order_sn AND ods.order_sn = oi.order_sn and ods.depot_code = mog.depot_code
GROUP BY moi.master_order_sn, mog.custom_code, mog.depot_code order by moi.add_time desc
limit ${start}, ${limit}
