SELECT oi.master_order_sn, oi.order_out_sn, oi.order_type, oi.split_status, oi.shop_name, oi.referer, oi.order_from, oi.channel_code, oi.order_status, oi.pay_status, oi.ship_status, oi.user_id, oi.user_name, DATE_FORMAT(oi.add_time, '%Y-%m-%d %H:%i:%S') add_time, oi.goods_count, oi.goods_decimal_count, oi.goods_amount, oi.total_fee, oi.money_paid, oi.discount, oi.question_status, oai.consignee, oai.mobile, oai.tel, oai.address, oai.user_shop_name, oi.shipping_total_fee, oie.shop_code store_code, oie.shop_name store_name, oie.user_bank_no userBankNo, oie.user_pay_apply userPayApply, oie.order_type createOrderType, oi.instead_user_id instead_user_id, mop.pay_name as payMethod, oie.company_name as companyName, oie.company_code as companyCode FROM master_order_info oi, master_order_address_info oai, master_order_info_extend oie, master_order_pay mop
