SELECT oi.master_order_sn as masterOrderSn, oi.order_out_sn as orderOutSn, oi.order_type as orderType, oi.referer, oi.order_from as orderFrom, oi.channel_code as channelCode, oi.order_status as orderStatus, oi.pay_status as payStatus, oi.ship_status as shipStatus, oi.user_id as userId, DATE_FORMAT(oi.add_time, '%Y-%m-%d %H:%i:%S') as addTime, oi.instead_user_id as insteadUserId, oai.consignee, oai.mobile, oai.address, oie.company_name as companyName, oie.company_code as companyId, mog.goods_name as goodsName, mog.goods_sn as goodsSn, mog.custom_code as customCode, mog.extension_code as extensionCode, mog.depot_code as depotCode, sum(mog.goods_number) as goodsNumber, sum(mog.goods_decimal_number) as goodsDecimalNumber, mog.goods_price as goodsPrice, sum(mog.goods_price) as totalGoodsPrice, mog.transaction_price as transactionPrice, sum(mog.transaction_price) as totalTransactionPrice, mog.settlement_price as settlementPrice, mog.discount, mog.share_bonus as shareBonus, mog.promotion_id as promotionId, mog.sap, mog.input_tax as inputTax, mog.output_tax as outputTax, mog.cost_price as costPrice, mog.box_gauge as boxGauge, mop.pay_name as payMethod, mog.customer_material_code as customerMaterialCode, mog.customer_material_name as customerMaterialName, mog.supplier_code as supplierCode FROM master_order_info oi, master_order_address_info oai, master_order_info_extend oie, master_order_goods mog, master_order_pay mop
group by oi.master_order_sn, mog.custom_code, mog.extension_code, mog.goods_price, mog.transaction_price, mog.settlement_price, mog.depot_code order by ${orderByClause}
limit ${criterion.value}, ${criterion.secondValue}

